import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Login from './components/Login';
import Register from './components/Register';
import LecturerDashboard from './components/LecturerDashboard';
import PRLDashboard from './components/PRLDashboard';
import PLDashboard from './components/PLDashboard';
import StudentDashboard from './components/StudentDashboard';
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  const role = token ? JSON.parse(atob(token.split('.')[1])).role : null; // Decode JWT role

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={!token ? <Login /> : <Navigate to={`/${role.toLowerCase()}`} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/lecturer" element={token && role === 'Lecturer' ? <LecturerDashboard /> : <Navigate to="/" />} />
          <Route path="/prl" element={token && role === 'PRL' ? <PRLDashboard /> : <Navigate to="/" />} />
          <Route path="/pl" element={token && role === 'PL' ? <PLDashboard /> : <Navigate to="/" />} />
          <Route path="/student" element={token && role === 'Student' ? <StudentDashboard /> : <Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
