import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Form, Button, Table, Container, Modal, InputGroup, FormControl, Alert, Row, Col } from 'react-bootstrap';

const StudentDashboard = () => {
  const [lectures, setLectures] = useState([]); // Fetch lectures somehow, e.g., public or assigned
  const [ratings, setRatings] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedLecture, setSelectedLecture] = useState(null);
  const [ratingData, setRatingData] = useState({ rating: 1, comments: '' });
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const config = { headers: { Authorization: `Bearer ${token}` } };

  useEffect(() => {
    // Assume fetch all lectures for monitoring
    fetchLectures();
  }, []);

  const fetchLectures = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/reports/Student', config); // Add route if needed
      setLectures(res.data);
    } catch (err) {
      setError('Failed to fetch lectures');
    }
  };

  const fetchRatings = async (lectureId) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/monitoring/${lectureId}`, config);
      setRatings(res.data);
    } catch (err) {
      setError('Failed to fetch ratings');
    }
  };

  const handleRatingSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/rating', { ...ratingData, lecture_id: selectedLecture.id }, config);
      setShowModal(false);
      setRatingData({ rating: 1, comments: '' });
      fetchRatings(selectedLecture.id);
      alert('Rating added');
    } catch (err) {
      setError('Rating failed');
    }
  };

  const handleSearch = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/search/lectures?q=${searchQuery}`, config);
      setLectures(res.data);
    } catch (err) {
      setError('Search failed');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <Container className="dashboard mt-5">
      <Row>
        <Col><h2>Student Dashboard - Monitoring & Rating</h2></Col>
        <Col className="text-end"><Button variant="outline-danger" onClick={logout}>Logout</Button></Col>
      </Row>
      {error && <Alert variant="danger">{error}</Alert>}
      <InputGroup className="mb-3">
        <FormControl placeholder="Search lectures..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
        <Button onClick={handleSearch}>Search</Button>
      </InputGroup>
      <Table striped bordered hover>
        <thead><tr><th>ID</th><th>Topic</th><th>Date</th><th>Actions</th></tr></thead>
        <tbody>
          {lectures.map(lecture => (
            <tr key={lecture.id}>
              <td>{lecture.id}</td>
              <td>{lecture.topic_taught}</td>
              <td>{lecture.date_of_lecture}</td>
              <td>
                <Button size="sm" onClick={() => { setSelectedLecture(lecture); fetchRatings(lecture.id); setShowModal(true); }}>Rate</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Rate Lecture {selectedLecture?.id}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleRatingSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Rating (1-5)</Form.Label>
              <Form.Select value={ratingData.rating} onChange={(e) => setRatingData({ ...ratingData, rating: parseInt(e.target.value) })}>
                {[1,2,3,4,5].map(r => <option key={r} value={r}>{r}</option>)}
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Comments</Form.Label>
              <Form.Control as="textarea" value={ratingData.comments} onChange={(e) => setRatingData({ ...ratingData, comments: e.target.value })} />
            </Form.Group>
            <Button type="submit">Submit Rating</Button>
          </Form>
          <h5 className="mt-3">Previous Ratings:</h5>
          <Table size="sm">
            <tbody>
              {ratings.map(r => (
                <tr key={r.id}><td>{r.rating} - {r.comments}</td></tr>
              ))}
            </tbody>
          </Table>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default StudentDashboard;
